		var ajax = new Ajax('/FraudSearch.nhn?keyword='+sKeyword+'&clubid='+sCafeId+'&type='+nType+'&token=V1Jha09DWUZoRE9RL21lSUR2dGlSUT09DQo',{
			method: 'GET',		
			suspend : true,
		    onLoad: function(res) {
				var oJsonResult = JSON.parse(res.responseText);
				if (oJsonResult.message.status == 200) {
					var oResult = oJsonResult.message.result;
