		var ajax = new Ajax('/FraudSearch.nhn?keyword='+sKeyword+'&clubid='+sCafeId+'&type='+nType+'&token=c1ZSSlNFcGNpeTZrTVROd2EyVlBYZz09DQo',{
			method: 'GET',		
			suspend : true,
		    onLoad: function(res) {
				var oJsonResult = JSON.parse(res.responseText);
				if (oJsonResult.message.status == 200) {
					var oResult = oJsonResult.message.result;
