						//elFraudLayerList.innerHTML = errorMessage;
						alert('숫자만 입력할 수 있습니다.');
						return;
					}					
					if(oResult.count > 0) {
						elFraudLayerList.innerHTML = makeFraudInformationHTML('true',oResult.count);
					} else {
						elFraudLayerList.innerHTML =  makeFraudInformationHTML('false','0');
