					Element.show('fraudLayerAddinfo');
				} else {
					//elFraudLayerList.innerHTML = errorMessage;
					alert('숫자만 입력할 수 있습니다.');
					return;
				}

			}
		});
		ajax.request();
};

function makeFraudInformationHTML(isFraud , count) {
	var fraudInformationText = '<ul><li class="complain_commerce';
